async function apiPost(t,r){const n=new URLSearchParams;n.append("json",JSON.stringify(r));const o=await fetch(t,{method:"POST",headers:{"Content-Type":"application/x-www-form-urlencoded"},body:n.toString()});if(!o.ok)throw new Error(`HTTP error! status: ${o.status}`);return o.json()}async function apiGet(t,r={}){const n=new URLSearchParams(r).toString(),o=n?`${t}?${n}`:t,a=await fetch(o);if(!a.ok)throw new Error(`HTTP error! status: ${a.status}`);return a.json()}