const GASActions={async post(t){const e=CONFIG.getAdminApiUrl(),r=new FormData;r.append("json",JSON.stringify(t));const a=await fetch(e,{method:"POST",body:r});if(!a.ok){const t=await a.text();throw new Error(`HTTP ${a.status}: ${t}`)}const n=await a.json();if(n.error)throw new Error(n.error);return n},async create(t,e){return this.post({action:"create",sheet:t,data:e})},async update(t,e,r){return this.post({action:"update",sheet:t,id:e,data:r})},async delete(t,e){return this.post({action:"delete",sheet:t,id:e})}};