const GASActions={async post(t){const e=CONFIG.getAdminApiUrl(),s=new FormData;s.append("json",JSON.stringify(t));const n=await fetch(e,{method:"POST",body:s});if(!n.ok){const t=await n.text();throw new Error(`HTTP ${n.status}: ${t}`)}const r=await n.json();if(r.error)throw new Error(r.error);return r},async create(t,e){return this.post({action:"create",sheet:t,data:e})},async update(t,e,s){return this.post({action:"update",sheet:t,id:e,data:s})},async delete(t,e){return this.post({action:"delete",sheet:t,id:e})},async upsertSetting(t,e){return this.post({action:"upsert_setting",sheet:"settings",data:{key:String(t),value:String(e)}})}};